import React, { useState } from 'react'
import {
  CCard,
  CCardHeader,
  CCardBody,
  CToast,
  CToastBody,
  CToastHeader,
  CToaster,
  CForm,
  CInput,
  CInputCheckbox,
  CButton,
  CContainer,
  CRow,
  CCol,
  CFormGroup,
  CLabel
} from '@coreui/react'

const Toaster = () => {

  const positions = [
    'static',
    'top-left',
    'top-center',
    'top-right',
    'top-full',
    'bottom-left',
    'bottom-center',
    'bottom-right',
    'bottom-full'
  ]

  const [toasts, setToasts] = useState([
    { position: 'static'},
    { position: 'static'},
    { position: 'top-right', autohide: 3000 }
  ])

  const [position, setPosition] = useState('top-right')
  const [autohide, setAutohide] = useState(true)
  const [autohideValue, setAutohideValue] = useState(5000)
  const [closeButton, setCloseButton] = useState(true)
  const [fade, setFade] = useState(true)

  const addToast = () => {
    setToasts([
      ...toasts, 
      { position, autohide: autohide && autohideValue, closeButton, fade }
    ])
  }


  const toasters = (()=>{
    return toasts.reduce((toasters, toast) => {
      toasters[toast.position] = toasters[toast.position] || []
      toasters[toast.position].push(toast)
      return toasters
    }, {})
  })()


  return (
    <CCard>
      <CCardHeader>
        Toasts.
      </CCardHeader>
      <CCardBody>
        <CContainer>
          <CRow>
            <CCol sm="12" lg="6">
              <CForm>
                <h5>Add toast with following props:</h5>

                <CFormGroup variant="custom-checkbox" className="my-2 mt-4">
                  <CInputCheckbox
                    id="autohide"
                    checked={autohide}
                    onChange={e => { setAutohide(e.target.checked) }}
                    custom
                  />
                  <CLabel variant="custom-checkbox" htmlFor="autohide">
                    Autohide of the toast
                  </CLabel>
                </CFormGroup>
                {
                  autohide &&
                  <CFormGroup className="my-2">
                    <CLabel htmlFor="ccyear">Time to autohide</CLabel>
                    <CInput
                      type="number"
                      value={autohideValue}
                      onChange={e => {
                        setAutohideValue(Number(e.target.value))
                      }}
                    />
                  </CFormGroup>
                }

                <CFormGroup className="my-2">
                  <CLabel htmlFor="ccyear">Position</CLabel>
                  <select
                    className="form-control"
                    value={position}
                    onChange={e => {setPosition(e.target.value)}}
                  >
                    {
                      positions.map((position, i)=>(
                        <option key={i}>{position}</option>
                      ))
                    }
                  </select>
                </CFormGroup>

                <CFormGroup variant="custom-checkbox" className="my-2">
                  <CInputCheckbox
                    id="fade"
                    checked={fade}
                    onChange={e => { setFade(e.target.checked) }}
                    custom
                  />
                  <CLabel variant="custom-checkbox" htmlFor="fade">fade</CLabel>
                </CFormGroup>

                <CFormGroup variant="custom-checkbox" className="my-2">
                  <CInputCheckbox
                    id="close"
                    custom
                    checked={closeButton}
                    onChange={e=> { setCloseButton(e.target.checked) }}
                  />
                  <CLabel variant="custom-checkbox" htmlFor="close">
                    closeButton
                  </CLabel>
                </CFormGroup>

                <CButton
                  className="mr-1 w-25"
                  color="success"
                  onClick={addToast}
                >
                  Add toast
                </CButton>

              </CForm>
            </CCol>
            <CCol sm="12" lg="6">
              {Object.keys(toasters).map((toasterKey) => (
                <CToaster
                  position={toasterKey}
                  key={'toaster' + toasterKey}
                >
                  {
                    toasters[toasterKey].map((toast, key)=>{
                    return(
                      <CToast
                        key={'toast' + key}
                        show={true}
                        autohide={toast.autohide}
                        fade={toast.fade}
                      >
                        <CToastHeader closeButton={toast.closeButton}>
                          Toast title
                        </CToastHeader>
                        <CToastBody>
                          {`This is a toast in ${toasterKey} positioned toaster number ${key + 1}.`}
                        </CToastBody>
                      </CToast>
                    )
                  })
                  }
                </CToaster>
              ))}
            </CCol>
          </CRow>
        </CContainer>
      </CCardBody>
    </CCard>
  )
}

export default Toaster
